                          if(show_default_thumbnail == true && image_url == "") image_url= default_thumbnail;
                          image_tag='<img src="'+image_url.replace('/s72-c/','/w'+image_size+'/')+'" alt="'+post_title+'"/>';
                          if(image_url!="") return image_tag; else return "";
